targets.expr:
(
    (
        sum(echo_request_duration_seconds_bucket{le="$target", code=~"^2..$", instance=~"$instance"})
        + (
            sum(echo_request_duration_seconds_bucket{le="$tolerated", code=~"^2..$", instance=~"$instance"})
            - sum(echo_request_duration_seconds_bucket{le="$target", code=~"^2..$", instance=~"$instance"})
        ) / 2
    ) / sum(echo_request_duration_seconds_count{instance=~"$instance", code=~"^2..$"})
) * 100

(
    (
        sum(echo_request_duration_seconds_bucket{le="$target", code=~"^2..$", instance=~"$instance"}) by (instance)
        + (
            sum(echo_request_duration_seconds_bucket{le="$tolerated", code=~"^2..$", instance=~"$instance"}) by (instance)
            - sum(echo_request_duration_seconds_bucket{le="$target", code=~"^2..$", instance=~"$instance"}) by (instance)
        ) / 2
    ) / sum(echo_request_duration_seconds_count{instance=~"$instance", code=~"^2..$"}) by (instance)
) * 100
